mapper.select:

	select mem_id 
	       ,mem_password
     from  pj_member
    where  mem_id = #{memId} 
      and  mem_password = #{memPassword}

mapper.insert:
insert into PJ_member
	(mem_id, mem_name, mem_password, mem_joindate)
values (member_seq.nextval, #{memName}, #{memPassword}, sysdate);

mapper.select:
select mem_id as memId
      ,mem_name as memName
      ,mem_joindate as memJoinDate
from PJ_member where 1=1

	and mem_id = #{memId} 


	and mem_name = #{memName} 


	and memJoinDate = #{memJoinDate}
